import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/common/Sidebar';
import Header from '../components/common/Header';
import CalendarView from '../components/practitioner/CalendarView';
import PatientList from '../components/practitioner/PatientList';
import InsightsPanel from '../components/practitioner/InsightsPanel';
import { NAVIGATION_ITEMS } from '../constants';
import api from '../services/api';

const PractitionerDashboard = () => {
  const [activeItem, setActiveItem] = useState('dashboard');
  const [pendingAppointments, setPendingAppointments] = useState([]);
  const [dashboardStats, setDashboardStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user') || '{}');

    if (!token || user.role !== 'practitioner') {
      navigate('/login');
      return;
    }

    fetchPendingAppointments();
    fetchDashboardStats();

    // Set up real-time data refresh every 30 seconds
    const interval = setInterval(() => {
      fetchDashboardStats();
      fetchPendingAppointments();
    }, 30000);

    return () => clearInterval(interval);
  }, [navigate]);

  const fetchPendingAppointments = async () => {
    try {
      const response = await api.get('/appointments');
      // Filter for scheduled and confirmed appointments
      const activeAppointments = response.data.filter(apt => 
        apt.status === 'scheduled' || apt.status === 'confirmed'
      );
      setPendingAppointments(activeAppointments);
    } catch (error) {
      console.error('Error fetching pending appointments:', error);
    }
  };

  const fetchDashboardStats = async () => {
    try {
      const response = await api.get('/practitioners/dashboard/stats');
      setDashboardStats(response.data);
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    }
  };

  const handleAcceptAppointment = async (appointmentId) => {
    setLoading(true);
    try {
      await api.patch(`/appointments/${appointmentId}/accept`);
      alert('Appointment accepted successfully!');
      fetchPendingAppointments();
      fetchDashboardStats();
    } catch (error) {
      alert('Error accepting appointment');
    } finally {
      setLoading(false);
    }
  };

  const handleRejectAppointment = async (appointmentId) => {
    const reason = prompt('Please provide a reason for rejection:');
    if (!reason) return;

    setLoading(true);
    try {
      await api.patch(`/appointments/${appointmentId}/reject`, { reason });
      alert('Appointment rejected successfully!');
      fetchPendingAppointments();
      fetchDashboardStats();
    } catch (error) {
      alert('Error rejecting appointment');
    } finally {
      setLoading(false);
    }
  };

  const handleCompleteAppointment = async (appointmentId) => {
    if (!window.confirm('Mark this appointment as completed? This will update your earnings.')) return;

    setLoading(true);
    try {
      await api.patch(`/appointments/${appointmentId}/complete`);
      alert('Appointment completed! Earnings updated.');
      fetchPendingAppointments();
      fetchDashboardStats();
    } catch (error) {
      alert('Error completing appointment');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    navigate('/login');
  };

  const renderContent = () => {
    switch (activeItem) {
      case 'dashboard':
        return (
          <div className="space-y-6">
            <div className="grid lg:grid-cols-4 gap-6">
              <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-blue-100 text-sm">Today's Sessions</p>
                    <p className="text-3xl font-bold">{dashboardStats?.todayAppointments || 0}</p>
                    <p className="text-blue-100 text-sm">{dashboardStats?.remainingToday || 0} remaining</p>
                  </div>
                  <div className="text-4xl opacity-80">📅</div>
                </div>
              </div>
              <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-green-100 text-sm">Total Patients</p>
                    <p className="text-3xl font-bold">{dashboardStats?.totalPatients || 0}</p>
                    <p className="text-green-100 text-sm">All time</p>
                  </div>
                  <div className="text-4xl opacity-80">👥</div>
                </div>
              </div>
              <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-purple-100 text-sm">Avg Rating</p>
                    <p className="text-3xl font-bold">{dashboardStats?.avgRating || 4.5}</p>
                    <p className="text-purple-100 text-sm">⭐⭐⭐⭐⭐</p>
                  </div>
                  <div className="text-4xl opacity-80">🏆</div>
                </div>
              </div>
              <div className="bg-gradient-to-r from-orange-500 to-orange-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-orange-100 text-sm">Revenue</p>
                    <p className="text-3xl font-bold">₹{dashboardStats?.monthlyRevenue ? (dashboardStats.monthlyRevenue >= 1000 ? (dashboardStats.monthlyRevenue / 1000).toFixed(1) + 'K' : dashboardStats.monthlyRevenue) : '0'}</p>
                    <p className="text-orange-100 text-sm">This month</p>
                  </div>
                  <div className="text-4xl opacity-80">💰</div>
                </div>
              </div>
            </div>
            {/* Pending Appointments */}
            {pendingAppointments.length > 0 && (
              <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6 mb-6">
                <h3 className="text-lg font-semibold text-ayur-primary mb-4">
                  Active Appointments ({pendingAppointments.length})
                </h3>
                <div className="space-y-3">
                  {pendingAppointments.map((appointment) => (
                    <div key={appointment._id} className="flex items-center justify-between p-4 bg-yellow-50 rounded-xl border border-yellow-200">
                      <div className="flex-1">
                        <div className="flex items-center space-x-4">
                          <div className="w-10 h-10 bg-ayur-primary text-white rounded-full flex items-center justify-center font-semibold">
                            {appointment.patient.name.charAt(0)}
                          </div>
                          <div className="flex-1">
                            <p className="font-semibold text-gray-900">{appointment.patient.name}</p>
                            <p className="text-sm text-gray-600">{appointment.therapy.name} • ₹{appointment.totalAmount}</p>
                            <p className="text-sm text-gray-500">
                              {new Date(appointment.date).toLocaleDateString()} at {appointment.timeSlot}
                            </p>
                            {appointment.notes && (
                              <p className="text-xs text-gray-400 mt-1">Note: {appointment.notes}</p>
                            )}
                          </div>
                          <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                            appointment.status === 'scheduled' ? 'bg-yellow-100 text-yellow-800' :
                            appointment.status === 'confirmed' ? 'bg-green-100 text-green-800' :
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {appointment.status.charAt(0).toUpperCase() + appointment.status.slice(1)}
                          </div>
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        {appointment.status === 'scheduled' && (
                          <>
                            <button
                              onClick={() => handleAcceptAppointment(appointment._id)}
                              disabled={loading}
                              className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50"
                            >
                              Accept
                            </button>
                            <button
                              onClick={() => handleRejectAppointment(appointment._id)}
                              disabled={loading}
                              className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 disabled:opacity-50"
                            >
                              Reject
                            </button>
                          </>
                        )}
                        {appointment.status === 'confirmed' && (
                          <button
                            onClick={() => handleCompleteAppointment(appointment._id)}
                            disabled={loading}
                            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50"
                          >
                            Complete
                          </button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="grid lg:grid-cols-3 gap-6">
              <div className="lg:col-span-2">
                <CalendarView />
              </div>
              <div>
                <PatientList />
              </div>
            </div>
          </div>
        );
      case 'calendar':
        return <CalendarView />;
      case 'patients':
        return <PatientList />;
      case 'insights':
        return <InsightsPanel />;
      default:
        return <div className="card">Dashboard</div>;
    }
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-ayur-light/30 via-white to-ayur-accent/10">
      <Sidebar 
        items={NAVIGATION_ITEMS.PRACTITIONER}
        activeItem={activeItem}
        onItemClick={setActiveItem}
      />
      <div className="flex-1 flex flex-col min-w-0">
        <Header title="Practitioner Dashboard" userType="practitioner" onLogout={handleLogout} />
        <main className="flex-1 p-4 overflow-auto">
          <div className="max-w-full">
            <div className="mb-4">
              <h2 className="text-xl font-bold text-ayur-primary mb-1">
                Good morning, {dashboardStats?.practitionerName || 'Doctor'}!
              </h2>
              <p className="text-sm text-gray-600">
                {dashboardStats?.specialization || 'Ayurveda Specialist'} • Manage your practice and help patients
              </p>
            </div>
            {renderContent()}
          </div>
        </main>
      </div>
    </div>
  );
};

export default PractitionerDashboard;
