import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Card from '../components/common/Card';
import GlobalHeader from '../components/common/GlobalHeader';
import GlobalFooter from '../components/common/GlobalFooter';
import { TIME_SLOTS } from '../constants';
import api from '../services/api';

const Booking = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    therapy: '',
    practitioner: '',
    date: '',
    timeSlot: '',
    notes: ''
  });
  const [selectedPractitioner, setSelectedPractitioner] = useState(null);
  const [practitioners, setPractitioners] = useState([]);
  const [therapies, setTherapies] = useState([]);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchPractitioners();
    fetchTherapies();
  }, []);

  useEffect(() => {
    if (formData.practitioner && formData.date) {
      fetchAvailableSlots();
    }
  }, [formData.practitioner, formData.date]);

  const fetchPractitioners = async () => {
    try {
      const response = await api.get('/practitioners');
      setPractitioners(response.data);
    } catch (error) {
      console.error('Error fetching practitioners:', error);
    }
  };

  const fetchTherapies = async () => {
    try {
      const response = await api.get('/therapies');
      setTherapies(response.data);
    } catch (error) {
      console.error('Error fetching therapies:', error);
    }
  };

  const fetchAvailableSlots = async () => {
    try {
      const response = await api.get(`/appointments/slots/${formData.practitioner}/${formData.date}`);
      setAvailableSlots(response.data);
    } catch (error) {
      console.error('Error fetching available slots:', error);
      setAvailableSlots(TIME_SLOTS); // Fallback to all slots
    }
  };

  const calculatePrice = () => {
    const therapy = therapies.find(t => t.name === formData.therapyType);
    if (therapy) {
      const duration = therapy.durations.find(d => d.minutes === parseInt(formData.duration));
      return duration ? duration.price : 0;
    }
    return 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const bookingData = {
        practitioner: formData.practitioner,
        therapyType: formData.therapyType,
        date: formData.date,
        time: formData.time,
        duration: parseInt(formData.duration),
        price: calculatePrice()
      };

      await api.post('/bookings', bookingData);
      alert('Booking created successfully!');
      navigate('/patient');
    } catch (error) {
      alert(error.response?.data?.message || 'Booking failed');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const isSlotAvailable = (time) => {
    return Math.random() > 0.3;
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <GlobalHeader />
      <div className="max-w-4xl mx-auto px-4 py-8">


        <Card title="Book Your Therapy Session" className="animate-slide-up">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Therapy Type
              </label>
              <select
                name="therapyType"
                value={formData.therapyType}
                onChange={handleChange}
                required
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-ayur-primary focus:border-transparent"
              >
                <option value="">Select Therapy</option>
                {therapies.map(therapy => (
                  <option key={therapy._id} value={therapy.name}>{therapy.name}</option>
                ))}
              </select>
              {formData.therapyType && (() => {
                const therapy = therapies.find(t => t.name === formData.therapyType);
                return therapy ? (
                  <div className="mt-2 p-3 bg-ayur-light rounded-lg">
                    <p className="text-sm text-ayur-primary font-medium">
                      {therapy.description}
                    </p>
                  </div>
                ) : null;
              })()}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Preferred Date
              </label>
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
                required
                min={new Date().toISOString().split('T')[0]}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-ayur-primary focus:border-transparent"
              />
            </div>

            {/* Practitioner Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-4">
                Select Practitioner
              </label>
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
                {practitioners.map(practitioner => (
                  <div
                    key={practitioner.id}
                    onClick={() => {
                      setFormData({...formData, practitioner: practitioner._id});
                      setSelectedPractitioner(practitioner);
                    }}
                    className={`p-4 rounded-2xl border-2 cursor-pointer transition-all duration-300 ${
                      formData.practitioner === practitioner._id
                        ? 'border-ayur-primary bg-ayur-light shadow-md'
                        : 'border-gray-200 hover:border-ayur-accent hover:shadow-sm'
                    }`}
                  >
                    <div className="text-center mb-3">
                      <div className="text-4xl mb-2">👨⚕️</div>
                      <h4 className="font-semibold text-ayur-primary">{practitioner.name}</h4>
                      <p className="text-sm text-gray-600">{practitioner.practitionerDetails?.specialization}</p>
                    </div>
                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-gray-600">Experience:</span>
                        <span className="font-medium">{practitioner.practitionerDetails?.experience}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Rating:</span>
                        <span className="font-medium">{practitioner.practitionerDetails?.rating || 4.8} ⭐</span>
                      </div>
                    </div>
                    <p className="text-xs text-gray-500 mt-2">{practitioner.practitionerDetails?.description}</p>
                  </div>
                ))}
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Available Time Slots
              </label>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
                {(availableSlots.length > 0 ? availableSlots : TIME_SLOTS).map(time => {
                  const available = availableSlots.includes(time) || availableSlots.length === 0;
                  return (
                    <button
                      key={time}
                      type="button"
                      onClick={() => available && setFormData({...formData, time})}
                      disabled={!available || !selectedPractitioner}
                      className={`p-3 rounded-lg border text-sm font-medium ${
                        formData.time === time
                          ? 'bg-ayur-primary text-white border-ayur-primary'
                          : available && selectedPractitioner
                          ? 'bg-white text-ayur-primary border-ayur-primary hover:bg-ayur-light'
                          : 'bg-gray-100 text-gray-400 border-gray-200 cursor-not-allowed'
                      }`}
                    >
                      {time}
                      {!available && <div className="text-xs">Booked</div>}
                    </button>
                  );
                })}
              </div>
              {!selectedPractitioner && (
                <p className="text-sm text-gray-500 mt-2">Please select a practitioner first</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Duration (minutes)
              </label>
              <select
                name="duration"
                value={formData.duration}
                onChange={handleChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-ayur-primary focus:border-transparent"
              >
                <option value="30">30 minutes</option>
                <option value="60">60 minutes</option>
                <option value="90">90 minutes</option>
                <option value="120">120 minutes</option>
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-ayur-primary hover:bg-ayur-secondary text-white py-4 rounded-2xl font-semibold transition-all duration-300 transform hover:scale-105 hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              disabled={loading || !formData.therapyType || !formData.practitioner || !formData.date || !formData.time}
            >
              {loading ? 'Booking...' : `Book Session - ₹${calculatePrice()}`}
            </button>
          </form>
        </Card>
      </div>
      <GlobalFooter />
    </div>
  );
};

export default Booking;
