import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { mockLogin } from '../services/mockAuth';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [userType, setUserType] = useState('patient');
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const { token, user } = await mockLogin(formData.email, formData.password);

      login(token, user);

      if (user.role === 'patient') navigate('/patient');
      else if (user.role === 'practitioner') navigate('/practitioner');
      else navigate('/admin');

    } catch (error) {
      alert(error.message || 'Login failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-ayur-light via-white to-ayur-accent/20 flex items-center justify-center p-4">
      <div className="w-full max-w-sm">
        {/* Logo */}
        <div className="text-center mb-6 animate-fade-in">
          <div className="w-12 h-12 bg-ayur-primary rounded-xl mx-auto mb-3 flex items-center justify-center">
            <span className="text-xl text-white">🌿</span>
          </div>
          <h1 className="text-2xl font-bold text-ayur-primary">AyurSutra</h1>
          <p className="text-sm text-gray-600">Welcome back</p>
        </div>

        {/* Login Card */}
        <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg p-6 border border-white/20 animate-slide-up">
          {/* User Type Toggle */}
          <div className="flex bg-gray-100 rounded-xl p-1 mb-4">
            <button
              type="button"
              onClick={() => setUserType('patient')}
              className={`flex-1 py-2 px-2 rounded-lg text-xs font-medium transition-all duration-300 ${
                userType === 'patient'
                  ? 'bg-white text-ayur-primary shadow-sm'
                  : 'text-gray-600 hover:text-ayur-primary'
              }`}
            >
              Patient
            </button>
            <button
              type="button"
              onClick={() => setUserType('practitioner')}
              className={`flex-1 py-2 px-2 rounded-lg text-xs font-medium transition-all duration-300 ${
                userType === 'practitioner'
                  ? 'bg-white text-ayur-primary shadow-sm'
                  : 'text-gray-600 hover:text-ayur-primary'
              }`}
            >
              Practitioner
            </button>
            <button
              type="button"
              onClick={() => setUserType('admin')}
              className={`flex-1 py-2 px-2 rounded-lg text-xs font-medium transition-all duration-300 ${
                userType === 'admin'
                  ? 'bg-white text-ayur-primary shadow-sm'
                  : 'text-gray-600 hover:text-ayur-primary'
              }`}
            >
              Admin
            </button>
          </div>

          {/* Quick Demo Login */}
          <button
            type="button"
            onClick={async () => {
              setIsLoading(true);
              try {
                const demoCredentials = {
                  patient: { email: 'patient@ayursutra.com', password: 'demo123' },
                  practitioner: { email: 'practitioner@ayursutra.com', password: 'demo123' },
                  admin: { email: 'admin@ayursutra.com', password: 'admin123' }
                };

                const { token, user } = await mockLogin(
                  demoCredentials[userType].email,
                  demoCredentials[userType].password
                );

                login(token, user);

                if (user.role === 'patient') navigate('/patient');
                else if (user.role === 'practitioner') navigate('/practitioner');
                else navigate('/admin');
              } catch (error) {
                alert('Demo login failed: ' + error.message);
              } finally {
                setIsLoading(false);
              }
            }}
            disabled={isLoading}
            className="w-full flex items-center justify-center space-x-2 py-3 border border-gray-200 rounded-xl hover:border-ayur-primary hover:bg-ayur-light/20 transition-all duration-300 mb-4"
          >
            <span className="text-lg">🚀</span>
            <span className="text-sm font-medium text-gray-700">Quick Demo Login as {userType.charAt(0).toUpperCase() + userType.slice(1)}</span>
          </button>

          <div className="flex items-center mb-4">
            <div className="flex-1 border-t border-gray-300"></div>
            <span className="px-3 text-gray-500 text-xs">or</span>
            <div className="flex-1 border-t border-gray-300"></div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email address"
              required
              className="w-full px-4 py-3 bg-gray-50 border-0 rounded-xl focus:ring-2 focus:ring-ayur-primary focus:bg-white transition-all duration-300 text-sm"
            />
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Password"
              required
              className="w-full px-4 py-3 bg-gray-50 border-0 rounded-xl focus:ring-2 focus:ring-ayur-primary focus:bg-white transition-all duration-300 text-sm"
            />

            <button
              type="submit"
              disabled={isLoading}
              className="w-full bg-ayur-primary hover:bg-ayur-secondary text-white py-3 rounded-xl font-medium transition-all duration-300 disabled:opacity-70 disabled:cursor-not-allowed text-sm"
            >
              {isLoading ? (
                <div className="flex items-center justify-center space-x-2">
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                  <span>Signing in...</span>
                </div>
              ) : (
                `Sign in as ${userType.charAt(0).toUpperCase() + userType.slice(1)}`
              )}
            </button>
          </form>

          <div className="mt-4 text-center space-y-2">
            <p className="text-gray-600 text-xs">
              Don't have an account?{' '}
              <button
                onClick={() => navigate('/signup')}
                className="text-ayur-primary hover:text-ayur-secondary font-medium transition-colors duration-300"
              >
                Sign up
              </button>
            </p>
            <button
              onClick={() => navigate('/')}
              className="text-ayur-primary hover:text-ayur-secondary text-xs transition-colors duration-300"
            >
              ← Back to Home
            </button>
          </div>
        </div>

        {/* Demo Credentials */}
        <div className="mt-4 p-3 bg-white/60 backdrop-blur-sm rounded-xl border border-white/20 animate-fade-in-delay">
          <div className="text-xs text-gray-600 text-center space-y-1">
            <p><strong>Patient:</strong> patient@ayursutra.com / demo123</p>
            <p><strong>Practitioner:</strong> practitioner@ayursutra.com / demo123</p>
            <p><strong>Admin:</strong> admin@ayursutra.com / admin123</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
